routerContract = web3.eth.contract(address= routerAddress, abi=routerAbi)

symbol = myContract.functions.symbol().call()
decimals = myContract.functions.decimals().call()
DECIMAL = 10 ** decimals

def handle_event(event):
   #Web3.toJSON(event)
   #print(Web3.toJSON(event))
   try:
        value = event['args']['value']
        # print(value)
        txHashRaw = Web3.toJSON(event['transactionHash'])
        # print(txHashRaw)
        txHash =txHashRaw.strip('""')
        #print(txHash)
        tx = web3.eth.get_transaction(txHash)
        #print(tx)
        input = tx['input']
        decode = routerContract.decode_function_input(input)
        # print(decode[1])
        path = decode[1]['path']
        amountIn = decode[1]['amountIn']
        amountOutMin = decode[1]['amountOutMin']
        # print(path[0])
        ts = datetime.datetime.now()    
        #  print(path[-1])
        if path[-1] == contractA:
            myContract2 = web3.eth.contract(address=path[0], abi=contractAbi) 
            symbol2 = myContract2.functions.symbol().call()
            decimal2 = myContract2.functions.decimals().call()
            DECIMAL2 = 10 ** decimal2
            print(Fore.GREEN +'Buy '+ str(format(value / DECIMAL, ".3f"))+ ' ' + symbol + ' ' + str(format(amountIn / DECIMAL2, ".3f")) + ' ' + symbol2 + ' '  + str(ts)) 
        
        if path[-1] != contractA:
            myContract3 = web3.eth.contract(address=path[-1], abi=contractAbi) 
            symbol3 = myContract3.functions.symbol().call()
            decimals3 = myContract3.functions.decimals().call()
            DECIMAL3 = 10 ** decimals3
            print(Fore.RED +'Sell ' + str(format(value / DECIMAL, ".3f"))+ ' ' + symbol + ' ' + str(format(amountOutMin / DECIMAL3, ".3f")) + ' ' + symbol3 + ' '  + str(ts))

   except:
        pass



async def log_loop(event_filter, poll_interval):
    while True:
        for event in event_filter.get_new_entries():
            handle_event(event)
        await asyncio.sleep(poll_interval)

def main():
    event_filter = myContract.events.Transfer.createFilter(fromBlock="latest")
    #block_filter = web3.eth.filter('latest')
    #tx_filter = web3.eth.filter('pending')
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                #log_loop(block_filter, 2),
                #log_loop(tx_filter, 2)))
                log_loop(event_filter, 1)))
    finally:
        loop.close()

if __name__ == '__main__':
    main()
